const portfolioBlock = document.querySelector('.portfolio')
const typedBlock = document.querySelector('.header-typed')

if (portfolioBlock) {
    mixitup('.portfolio-mix', {
        selectors: {
            target: '.portfolio-mix__item',
        },
        animation: {
            duration: 400,
        },
    })
}

if (typedBlock) {
    new Typed('.typed', {
        stringsElement: '.header-typed',
        typeSpeed: 75,
        backSpeed: 75,
        loop: true,
        backDelay: 700,
    })
}

document.querySelectorAll('.menu-btn').forEach(function (menuBtn) {
    menuBtn.addEventListener('click', function () {
        document
            .querySelectorAll(
                '.navigation-menu, .navigation-menu__inner, .menu, .vertical-line-box, .vertical-line-box__inner, .menu-btn-open__inner, .menu-btn-close__inner, .nav-btn--mobile'
            )
            .forEach(function (element) {
                element.classList.toggle('active')
            })
        document.body.classList.toggle('lock')
    })
})

document
    .querySelectorAll('.nav-btn, .navigation-site__overlay')
    .forEach(function (element) {
        element.addEventListener('click', function () {
            document
                .querySelectorAll(
                    '.navigation-site, .navigation-site-window__inner, .navigation-site-window--opacity, .nav-btn__inner'
                )
                .forEach(function (el) {
                    el.classList.toggle('active')
                })
        })
    })
